import { useState, useEffect, useRef } from 'react'

export default function ImprovisationalStructureGenerator() {
  // State variables
  const [playerNames, setPlayerNames] = useState(['Lisa', 'Rebecca', 'Michael', 'Willie', 'Shivangi', 'Chieh'])
  const [newPlayerInput, setNewPlayerInput] = useState('')
  const [columns, setColumns] = useState(8)
  const [minPlayers, setMinPlayers] = useState(1)
  const [maxAdditionalPlayers, setMaxAdditionalPlayers] = useState(1)
  const [sliderPosition, setSliderPosition] = useState(-1)
  const [sliderActive, setSliderActive] = useState(false)
  const [sliderVisible, setSliderVisible] = useState(true)
  const [sliderDuration, setSliderDuration] = useState(30)
  const [generatedTable, setGeneratedTable] = useState([])
  const [selectedCell, setSelectedCell] = useState(null)
  const [cellMode, setCellMode] = useState('cell') // 'none' or 'cell'
  const [settingsCollapsed, setSettingsCollapsed] = useState(false)

  const sliderInterval = useRef(null)
  const fileInputRef = useRef(null)

  // Initialize table on first load
  useEffect(() => {
    generateEmptyTable()
  }, [playerNames, columns])

  // Slider animation
  useEffect(() => {
    if (sliderActive && sliderVisible) {
      sliderInterval.current = setInterval(() => {
        setSliderPosition(prev => {
          const newPos = prev + 1
          if (newPos >= columns) {
            setSliderActive(false)
            return -1
          }
          return newPos
        })
      }, (sliderDuration * 1000) / columns)
    } else if (!sliderActive && sliderInterval.current) {
      clearInterval(sliderInterval.current)
    }

    return () => {
      if (sliderInterval.current) clearInterval(sliderInterval.current)
    }
  }, [sliderActive, sliderVisible, columns, sliderDuration])

  // Generate empty table
  const generateEmptyTable = () => {
    const newTable = playerNames.map(name => Array(columns).fill(''))
    setGeneratedTable(newTable)
  }

  // Fill table with cues fairly distributed among players
  const fillTable = () => {
    const newTable = [...generatedTable]

    // Track how many cues each player has
    const playerCueCounts = Array(playerNames.length).fill(0)

    for (let col = 0; col < columns; col++) {
      // Calculate how many players to assign in this column
      const additionalPlayers = Math.floor(Math.random() * (maxAdditionalPlayers + 1))
      const totalPlayers = Math.min(minPlayers + additionalPlayers, playerNames.length)

      // Sort players by number of cues (ascending) to prioritize players with fewer cues
      const playerIndices = Array.from({ length: playerNames.length }, (_, i) => i)
        .sort((a, b) => playerCueCounts[a] - playerCueCounts[b])

      // Clear column first
      for (let row = 0; row < playerNames.length; row++) {
        newTable[row][col] = ''
      }

      // Assign cues to selected players (prioritizing those with fewer cues)
      for (let i = 0; i < totalPlayers; i++) {
        const playerIdx = playerIndices[i]
        newTable[playerIdx][col] = 'X'
        playerCueCounts[playerIdx]++
      }
    }

    setGeneratedTable(newTable)
  }

  // Handle player names from file
  const handleFileUpload = (e) => {
    const file = e.target.files[0]
    if (!file) return

    const reader = new FileReader()
    reader.onload = (e) => {
      const text = e.target.result
      const lines = text.split('\n')
      const names = lines
        .filter(line => line.trim())
        .map(line => {
          const match = line.match(/\d+,\s*\d+\s+([^;]+);/)
          return match ? match[1].trim() : null
        })
        .filter(Boolean)

      if (names.length > 0) {
        setPlayerNames(names)
      }
    }
    reader.readAsText(file)
  }

  // Handle manually adding players
  const handleAddPlayers = () => {
    if (!newPlayerInput.trim()) return

    const newPlayers = newPlayerInput
      .split(',')
      .map(name => name.trim())
      .filter(name => name)

    if (newPlayers.length > 0) {
      setPlayerNames([...playerNames, ...newPlayers])
      setNewPlayerInput('')
    }
  }

  // Handle removing a player
  const removePlayer = (index) => {
    const newPlayers = [...playerNames]
    newPlayers.splice(index, 1)
    setPlayerNames(newPlayers)
  }

  // Handle player reordering
  const movePlayer = (index, direction) => {
    if ((direction === 'up' && index === 0) || 
        (direction === 'down' && index === playerNames.length - 1)) {
      return
    }

    const newPlayers = [...playerNames]
    const targetIndex = direction === 'up' ? index - 1 : index + 1

    // Swap positions
    const temp = newPlayers[index]
    newPlayers[index] = newPlayers[targetIndex]
    newPlayers[targetIndex] = temp
    setPlayerNames(newPlayers)
  }

  // Toggle cell state
  const toggleCell = (rowIdx, colIdx) => {
    const newTable = [...generatedTable]
    newTable[rowIdx][colIdx] = newTable[rowIdx][colIdx] ? '' : 'X'
    setGeneratedTable(newTable)
  }

  // Clear all cells
  const clearAllCells = () => {
    generateEmptyTable()
  }

  // Start/reset slider
  const startSlider = () => {
    setSliderPosition(0)
    setSliderActive(true)
  }

  // Pause slider
  const pauseSlider = () => {
    setSliderActive(false)
  }

  // Reset slider
  const resetSlider = () => {
    setSliderPosition(-1)
    setSliderActive(false)
  }

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      {/* Main Container */}
      <div className="flex flex-grow overflow-hidden">
        {/* Settings Panel (Left Side) */}
        <div className={`bg-gray-200 transition-all duration-300 ${settingsCollapsed ? 'w-8' : 'w-1/4'} flex flex-col relative`}>
          {/* Collapse/Expand Toggle */}
          <button 
            onClick={() => setSettingsCollapsed(!settingsCollapsed)}
            className="absolute top-3 right-2 bg-blue-500 text-white rounded-full w-6 h-6 flex items-center justify-center z-10"
          >
            {settingsCollapsed ? '>' : '<'}
          </button>

          {!settingsCollapsed && (
            <div className="p-4 overflow-y-auto flex-grow">
              <h1 className="text-xl font-bold text-center mb-4">Settings</h1>

              {/* Players Management */}
              <div className="bg-yellow-100 p-3 rounded-lg mb-4">
                <h2 className="font-bold mb-2">Players</h2>

                {/* Add Players */}
                <div className="mb-3">
                  <label className="block mb-1 text-sm">Add Players</label>
                  <div className="flex gap-2">
                    <input 
                      type="text" 
                      value={newPlayerInput} 
                      onChange={(e) => setNewPlayerInput(e.target.value)}
                      placeholder="Names, separated by commas"
                      className="border border-gray-400 px-2 py-1 rounded text-sm flex-grow"
                    />
                    <button 
                      onClick={handleAddPlayers}
                      className="bg-green-500 text-white px-2 py-1 rounded text-sm"
                    >
                      Add
                    </button>
                  </div>
                </div>

                {/* File Upload */}
                <div className="mb-3">
                  <label className="block mb-1 text-sm">Player Names File</label>
                  <div className="flex gap-2">
                    <input 
                      type="file" 
                      accept=".txt" 
                      onChange={handleFileUpload} 
                      ref={fileInputRef}
                      className="hidden"
                    />
                    <button 
                      onClick={() => fileInputRef.current.click()}
                      className="bg-blue-100 border border-gray-400 px-2 py-1 rounded text-sm"
                    >
                      Open File
                    </button>
                    <button 
                      onClick={() => setPlayerNames([])}
                      className="bg-gray-200 border border-gray-400 px-2 py-1 rounded text-sm"
                    >
                      Clear All
                    </button>
                  </div>
                </div>

                {/* Player List */}
                <div className="mb-3">
                  <label className="block mb-1 text-sm">Current Players</label>
                  <div className="max-h-40 overflow-y-auto bg-white rounded border border-gray-300">
                    {playerNames.length === 0 ? (
                      <div className="p-2 text-gray-500 text-sm">No players added</div>
                    ) : (
                      <ul className="divide-y divide-gray-200">
                        {playerNames.map((name, index) => (
                          <li key={index} className="flex items-center justify-between p-2 hover:bg-gray-50">
                            <span className="text-sm">{name}</span>
                            <div className="flex gap-1">
                              <button 
                                onClick={() => movePlayer(index, 'up')}
                                disabled={index === 0}
                                className={`px-1 text-xs rounded ${index === 0 ? 'bg-gray-100 text-gray-400' : 'bg-blue-100'}`}
                              >
                                ↑
                              </button>
                              <button 
                                onClick={() => movePlayer(index, 'down')}
                                disabled={index === playerNames.length - 1}
                                className={`px-1 text-xs rounded ${index === playerNames.length - 1 ? 'bg-gray-100 text-gray-400' : 'bg-blue-100'}`}
                              >
                                ↓
                              </button>
                              <button 
                                onClick={() => removePlayer(index)}
                                className="bg-red-100 px-1 text-xs rounded"
                              >
                                ×
                              </button>
                            </div>
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                </div>
              </div>

              {/* Table Configuration */}
              <div className="bg-yellow-100 p-3 rounded-lg mb-4">
                <h2 className="font-bold mb-2">Table Settings</h2>

                <div className="grid grid-cols-2 gap-3">
                  <div>
                    <label className="block mb-1 text-sm">Number of Columns</label>
                    <input 
                      type="number" 
                      min="1" 
                      max="20" 
                      value={columns} 
                      onChange={(e) => setColumns(parseInt(e.target.value) || 1)}
                      className="border border-gray-400 px-2 py-1 rounded w-full text-sm"
                    />
                  </div>

                  <div>
                    <label className="block mb-1 text-sm">Min Players per Column</label>
                    <input 
                      type="number" 
                      min="0" 
                      max={playerNames.length} 
                      value={minPlayers} 
                      onChange={(e) => setMinPlayers(parseInt(e.target.value) || 0)}
                      className="border border-gray-400 px-2 py-1 rounded w-full text-sm"
                    />
                  </div>

                  <div>
                    <label className="block mb-1 text-sm">Max Additional Players</label>
                    <input 
                      type="number" 
                      min="0" 
                      max={playerNames.length} 
                      value={maxAdditionalPlayers} 
                      onChange={(e) => setMaxAdditionalPlayers(parseInt(e.target.value) || 0)}
                      className="border border-gray-400 px-2 py-1 rounded w-full text-sm"
                    />
                  </div>

                  <div className="flex items-end">
                    <button 
                      onClick={fillTable}
                      className="bg-red-400 text-white border border-gray-400 px-3 py-1 rounded text-sm"
                    >
                      Fill Table
                    </button>
                  </div>
                </div>
              </div>

              {/* Slider Controls */}
              <div className="bg-yellow-100 p-3 rounded-lg mb-4">
                <h2 className="font-bold mb-2">Slider Settings</h2>

                <div className="grid grid-cols-2 gap-3 mb-3">
                  <div>
                    <label className="block mb-1 text-sm">Duration (seconds)</label>
                    <input 
                      type="number" 
                      min="1" 
                      value={sliderDuration} 
                      onChange={(e) => setSliderDuration(parseInt(e.target.value) || 30)}
                      className="border border-gray-400 px-2 py-1 rounded w-full text-sm"
                    />
                  </div>

                  <div>
                    <label className="block mb-1 text-sm">Visibility</label>
                    <div className="flex gap-2">
                      <button 
                        onClick={() => setSliderVisible(true)}
                        className={`border px-2 py-1 rounded text-sm ${sliderVisible ? 'bg-orange-400 text-white' : 'bg-gray-200'}`}
                      >
                        Show
                      </button>
                      <button 
                        onClick={() => setSliderVisible(false)}
                        className={`border px-2 py-1 rounded text-sm ${!sliderVisible ? 'bg-gray-500 text-white' : 'bg-gray-200'}`}
                      >
                        Hide
                      </button>
                    </div>
                  </div>
                </div>

                <div className="flex gap-2">
                  <button 
                    onClick={startSlider} 
                    className="bg-green-200 border border-gray-400 px-3 py-1 rounded text-sm"
                  >
                    Start
                  </button>
                  <button 
                    onClick={pauseSlider}
                    className="bg-blue-200 border border-gray-400 px-3 py-1 rounded text-sm"
                  >
                    Pause
                  </button>
                  <button 
                    onClick={resetSlider}
                    className="bg-red-100 border border-gray-400 px-3 py-1 rounded text-sm"
                  >
                    Reset
                  </button>
                </div>
              </div>

              {/* Cell Options */}
              <div className="bg-yellow-100 p-3 rounded-lg mb-4">
                <h2 className="font-bold mb-2">Cell Mode</h2>
                <div className="flex gap-2">
                  <button 
                    onClick={() => setCellMode('none')}
                    className={`border px-3 py-1 rounded text-sm ${cellMode === 'none' ? 'bg-orange-400 text-white' : 'bg-gray-200'}`}
                  >
                    None
                  </button>
                  <button 
                    onClick={() => setCellMode('cell')}
                    className={`border px-3 py-1 rounded text-sm ${cellMode === 'cell' ? 'bg-gray-500 text-white' : 'bg-gray-200'}`}
                  >
                    Cell
                  </button>
                  <button 
                    onClick={clearAllCells}
                    className="bg-gray-200 border border-gray-400 px-3 py-1 rounded text-sm ml-auto"
                  >
                    Clear All
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Main Table Display (Right Side) */}
        <div className={`flex-grow ${settingsCollapsed ? 'w-full' : 'w-3/4'} p-4 overflow-auto flex flex-col`}>
          <h1 className="text-2xl font-bold mb-4 text-center">Improvisational Structure Generator</h1>

          <div className="overflow-auto flex-grow">
            <table className="border-collapse border w-full">
              <thead>
                <tr>
                  <th className="border border-gray-400 p-2 bg-gray-100 sticky left-0 z-10">Players</th>
                  {Array.from({ length: columns }, (_, i) => (
                    <th key={i} className="border border-gray-400 p-2 bg-gray-100 w-12 text-center">
                      {i + 1}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {playerNames.map((name, rowIdx) => (
                  <tr key={rowIdx}>
                    <td className="border border-gray-400 p-2 bg-gray-100 font-medium sticky left-0 z-10">
                      {name}
                    </td>
                    {Array.from({ length: columns }, (_, colIdx) => (
                      <td 
                        key={colIdx} 
                        className={`border border-gray-400 p-0 text-center relative h-12 w-12
                          ${colIdx === sliderPosition && sliderVisible ? 'bg-yellow-200' : 'bg-white'}
                        `}
                        onClick={() => cellMode === 'cell' ? toggleCell(rowIdx, colIdx) : null}
                      >
                        {generatedTable[rowIdx]?.[colIdx] && (
                          <div className="absolute inset-0 flex items-center justify-center font-bold text-xl">
                            X
                          </div>
                        )}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  )
}
